const lowLimit = process.argv[2]
const highLimit = process.argv[3]
const objectName = process.argv[4]

const bacnet = require('../bacnet.js')
const r = bacnet.init({
  datalink: {
    iface: 'wlan0',
    ip_port: '0xBAC0'
  },
  device: false
})

r.whoHasName(lowLimit, highLimit, objectName)

setTimeout(function () {}, 1000)
