const lowLimit = process.argv[2]
const highLimit = process.argv[3]
const objectType = process.argv[4]
const objectInstance = process.argv[5]

const bacnet = require('../bacnet.js')
const r = bacnet.init({
  datalink: {
    iface: 'wlan0',
    ip_port: '0xBAC0'
  },
  device: false
})

r.whoHasObject(lowLimit, highLimit, objectType, objectInstance)

setTimeout(function () {}, 1000)
