const deviceAddress = process.argv[2]
const deviceId = process.argv[3]
const timeDuration = process.argv[4]
const state = process.argv[5]
const password = process.argv[6]

const bacnet = require('../bacnet.js')
const r = bacnet.init({
  datalink: {
    iface: 'wlan0',
    ip_port: '0xBAC0'
  },
  device: false
})

r.deviceCommControl(deviceAddress, deviceId, timeDuration, state, password)

setTimeout(function () {}, 1000)
